def nb_journalists(journalists)
	puts '=' * 80
	puts ' ' * 5 + "NOMBRE DE JOURNALISTES " 
	puts '=' * 80
	puts
	puts "Il y a #{journalists.length} journalistes."
	puts
	return journalists.length
end

def nb_journalists_with_number_in_name(journalists)
	puts '=' * 80
	puts ' ' * 5 + "NOMBRE DE JOURNALISTES AVEC UN TWITTER INCLUANT UN CHIFFRE" 
	puts '=' * 80
	puts
	count = journalists.count {|journalist| journalist =~ /[0-9]/}
	puts "Il y a #{count} journalistes dont le pseudo inclut un chiffre."
	puts
	return count
end

def nb_journalists_with_Aude_in_name(journalists)
	puts '=' * 80
	puts ' ' * 5 + "NOMBRE DE JOURNALISTES AVEC UN TWITTER INCLUANT 'AUDE'" 
	puts '=' * 80
	puts
	count = journalists.count {|journalist| journalist.downcase =~ /aude/}
	puts "Il y a #{count} journalistes dont le pseudo inclut 'Aude'."
	puts

	return count
end

def nb_journalists_starting_with_uppercase(journalists)
	puts '=' * 80
	puts ' ' * 5 + "NOMBRE DE JOURNALISTES AVEC UN TWITTER COMMENCANT PAR UNE MAJUSCULE" 
	puts '=' * 80
	puts
	count = journalists.count {|journalist| journalist[1] =~ /[A-Z]/}
	puts "Il y a #{count} journalistes dont le pseudo commence par une majuscule."
	puts

	return count
end

