# Combien y a-t-il de underscore _ dans tous les pseudos confondus ?

def nb_journalists_with_underscore(journalists)
	puts '=' * 80
	puts ' ' * 5 + "NOMBRE DE UNDERSCORES DANS TOUS LES PSEUDOS"
	puts '=' * 80
	puts
	count = journalists.inspect.count('_')

	puts "Il y a #{count} underscores dans la liste de journalistes."
	puts

	return count
end

# Trie la liste de handle par ordre alphabétique.

def alphabetical_sort(journalists)
	puts '=' * 80
	puts ' ' * 5 + "CLASSEMENT EN ORDRE ALPHABETIQUE"
	puts '=' * 80
	puts
	journalists_sorted = journalists.sort_by { |word| word.downcase }
	puts journalists_sorted
	puts
	return journalists_sorted
end

# Quels sont les 50 handles les plus courts de ce array ?

def n_th_shortest_handles(journalists,n=50)
	puts '=' * 80
	puts ' ' * 5 + "50 HANDLES LES PLUS COURTS"
	puts '=' * 80
	puts
	journalists_sorted = journalists.sort_by { |word| word.length }
	n.times {puts journalists_sorted.shift()}
	puts
	return journalists_sorted
end

# Quelle est la position dans l'array de la personne @epenser ?

def position_in_array(journalists,handle)
	puts '=' * 80
	puts ' ' * 5 + "POSITION DANS LE TABLEAU DE #{handle}"
	puts '=' * 80
	puts 
	# puts "handle : #{handle}"
	if journalists.include?(handle)
		count = journalists.index(handle) + 1
		puts "Le handle #{handle} est à la #{count}ème position dans la liste de journalistes."
		puts
		return count
	else
		puts "Handle #{handle} non trouvé dans la liste de journalistes"
		puts
	end
end




